package carica;

public class caricaCorso {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int numero = 2;
		int carica = 2;
		
			
	for (int i = 0; i<15 ; i++) {
		carica = numero * carica;
				
		System.out.println(carica);		
	}
			
	int wa = 0;
	carica = 2;
	while (wa < 15) {
		wa++;
		carica = numero * carica;
		System.out.println(carica);
			
		}
	}

}
